package Jump_example;

/**
 * @author elsys
 *
 */
public class Jump_001 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i =1; i<=10; i++) {
			// or in java ||
			if (i==3|| i==5 || i==7) 
			{
				continue;  //break and continue jump inside the software
			}
			
			System.out.println(i);
		}
		

	}

}
